#!/bin/bash function kill_all_apps() { kill_app_with_port 9991 kill_app_with_port 9992 kill_app_with_port 9993 kill_app_with_port 9994 kill_app_with_port 9995 kill_app_with_port 9996 kill_app_with_port 9997 kill_app_with_port 9998 kill_app_with_port 9999 kill_app_with_port 8888 kill_app_with_port 8761 kill_app_with_port 9411 kill_app_with_port 9092 kill_app_with_port 2181 docker --tlsverify=false stop $(docker --tlsverify=false ps -a -q) || echo "No docker containers where active" docker --tlsverify=false kill $(docker --tlsverify=false ps -a -q) || echo "Nothing to kill" #docker --tlsverify=false rm $(docker --tlsverify=false ps -a -q) #docker --tlsverify=false rmi $(docker --tlsverify=false images -q) -f #docker volume ls -qf dangling=true | xargs -r docker volume rm #docker volume rm $(docker volume ls -qf dangling=true) pkill -f zookeeper || echo "No zookeeper process was running" pkill -f kafka || echo "No kafka process was running" }
# port is $1 function kill_app_with_port() { kill -9 $(lsof -t -i:$1) && echo "Killed an app running on port [$1]" || echo "No app running on port [$1]" }
kill_all_apps

#!/bin/bash

				function bootVersion {
				local bootVersion="${1}"
				curl --silent https://repo.spring.io/libs-snapshot-local/org/springframework/boot/spring-boot-starter/maven-metadata.xml | grep "<version>${bootVersion}." |
				grep "SNAPSHOT" | tail -1 | sed -ne '/<version>/s#\s*<[^>]*>\s*##gp' | xargs
				}


				function springCloudVersion {
				local cloudVersion="${1}"
				curl --silent https://repo.spring.io/libs-snapshot-local/org/springframework/cloud/spring-cloud-starter-build/maven-metadata.xml | grep "<version>${cloudVersion}."
				| grep "SNAPSHOT" | tail -1 | sed -ne '/<version>/s#\s*<[^>]*>\s*##gp' | xargs
				}


				./runAcceptanceTests.sh -t SLEUTH --killattheend -v "$( springCloudVersion "2022.0" )" --branch main -r -b "$( bootVersion "3.0" )"
